import React, { useEffect, useState } from "react";
import Logo from "../components/logo.png";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import axios from "axios";
import { API_DUMMY } from "../utils/api";
import Swal from "sweetalert2";
const NavbarAdmin = () => {
  const [profileAdmin, setProfileAdmin] = useState("");
  const role = localStorage.getItem("role");
  const [userMenuOpen, setUserMenuOpen] = useState(false);
  const [profileOrtu, setProfileOrtu] = useState([]);
  const id_ortu = localStorage.getItem("id_orangtua");

  const getAdmin = async () => {
    const id = localStorage.getItem("adminId");
    try {
      const admin = await axios.get(`${API_DUMMY}/api/admin/getById/${1}`);
      setProfileAdmin(admin.data.imageAdmin);
    } catch (error) {
      console.log(error);
    }
  };

  const getOrtu = async () => {
    try {
      const superAdmin = await axios.get(
        `${API_DUMMY}/api/orang-tua/getbyid/${id_ortu}`
      );
      setProfileOrtu(superAdmin.data.imageOrtu);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getAdmin();
    getOrtu();
  });

  const toggleUserMenu = () => {
    setUserMenuOpen(!userMenuOpen);
  };

  function logout() {
    // Tampilkan SweetAlert2 konfirmasi sebelum logout
    Swal.fire({
      title: "Logout",
      text: "Apakah Anda yakin ingin logout?",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Ya, Logout",
      cancelButtonText: "Batal",
    }).then((result) => {
      if (result.isConfirmed) {
        // Hapus item dari local storage saat logout
        localStorage.clear();
        Swal.fire({
          title: "Logout Berhasil",
          text: "Anda telah berhasil logout.",
          icon: "success",
          showConfirmButton: false,
          timer: 1000,
        }).then(() => {
          window.location.href = "/";
        });
      }
    });
  }

  return (
    <nav className="fixed top-0 z-50 w-full bg-indigo-500 border-b border-gray-200 dark:bg-gray-800 dark:border-gray-700">
      <div className="px-3 py-3 lg:px-5 lg:pl-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center justify-start rtl:justify-end">
            <button
              data-drawer-target="logo-sidebar"
              data-drawer-toggle="logo-sidebar"
              aria-controls="logo-sidebar"
              type="button"
              className="inline-flex items-center p-2 text-sm text-gray-500 rounded-lg sm:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200 dark:text-gray-400 dark:hover:bg-indigo-500 dark:focus:ring-gray-600"
            >
              <span className="sr-only">Open sidebar</span>
              <svg
                className="w-6 h-6"
                aria-hidden="true"
                fill="currentColor"
                viewBox="0 0 20 20"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  clipRule="evenodd"
                  fillRule="evenodd"
                  d="M2 4.75A.75.75 0 012.75 4h14.5a.75.75 0 010 1.5H2.75A.75.75 0 012 4.75zm0 10.5a.75.75 0 01.75-.75h7.5a.75.75 0 010 1.5h-7.5a.75.75 0 01-.75-.75zM2 10a.75.75 0 01.75-.75h14.5a.75.75 0 010 1.5H2.75A.75.75 0 012 10z"
                ></path>
              </svg>
            </button>
            <a href="" className="flex ms-2 md:me-24">
              <img src={Logo} className="h-11 me-3 text-white" alt="" />
              <span className="self-center text-xl font-semibold sm:text-xl whitespace-nowrap text-white">
                Presensi App
              </span>
            </a>
          </div>
          <div className="flex items-center">
            <div className="flex items-center ms-3">
              <div>
                <button
                  type="button"
                  className="flex text-sm bg-gray-800 rounded-full focus:ring-4 focus:ring-gray-300 dark:focus:ring-gray-600"
                  onClick={toggleUserMenu}
                  id="user-menu-button"
                  aria-expanded={userMenuOpen}
                  aria-haspopup="true"
                >
                  <span className="sr-only">Open user menu</span>
                  {localStorage.getItem("role") == "ADMIN" ? (
                    <>
                      {" "}
                      <img
                        className="w-8 h-8 rounded-full"
                        src={
                          profileAdmin
                            ? profileAdmin
                            : "data:image/jpeg;base64,"
                        }
                        alt="user photo"
                      />
                    </>
                  ) : localStorage.getItem("role") == "Wali Murid" ? (
                    <>
                      {" "}
                      <img
                        className="w-8 h-8 rounded-full"
                        src={
                          profileOrtu
                            ? profileOrtu
                            : "data:image/jpeg;base64,"
                        }
                        alt="user photo"
                      />
                    </>
                  ) : (
                    <></>
                  )}
                </button>
                {userMenuOpen && (
                  <div
                    role="menu"
                    aria-orientation="vertical"
                    aria-labelledby="user-menu-button"
                    className="absolute right-0 mt-2 w-48 bg-white dark:bg-gray-800 rounded-md shadow-lg py-1"
                    tabIndex="-1"
                  >
                    {localStorage.getItem("role") == "ADMIN" ? (
                      <>
                        <Link to="/admin/profil">
                          <button
                            role="menuitem"
                            tabIndex="-1"
                            id="user-menu-item-0"
                            className="block px-4 py-2 text-sm text-gray-700 dark:text-white"
                          >
                            Profile
                          </button>
                        </Link>
                      </>
                    ) : localStorage.getItem("role") == "Wali Murid" ? (
                      <>
                        <Link to="/walimurid/profile">
                          <button
                            role="menuitem"
                            tabIndex="-1"
                            id="user-menu-item-0"
                            className="block px-4 py-2 text-sm text-gray-700 dark:text-white"
                          >
                            Profile
                          </button>
                        </Link>
                      </>
                    ) : (
                      <></>
                    )}
                    <button
                      onClick={(e) => {
                        e.preventDefault();
                        logout();
                      }}
                      role="menuitem"
                      tabIndex="-1"
                      id="user-menu-item-2"
                      className="block px-4 py-2 text-sm text-gray-700 dark:text-white w-full text-left"
                    >
                      Keluar
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default NavbarAdmin;
